/*
 * Gets the ODBC state for the given handle type and handle.
 */
static SQLCHAR *
getstate(SQLSMALLINT handle_type, SQLHANDLE handle)
{
    SQLRETURN ret;
    SQLCHAR *state;

    state = (SQLCHAR *)malloc(STATE_LEN + 1);
    ret = SQLGetDiagRec(handle_type, handle, 1, state, NULL, NULL, 0, NULL);
    if (ret != SQL_SUCCESS 
    && ret != SQL_SUCCESS_WITH_INFO
    && ret != SQL_NO_DATA)
        state = (SQLCHAR *)"ERROR";

    return state;
}

/*
 * Gets the error message string for the given ODBC state.
 */
static SQLCHAR *
getmsg(SQLCHAR *state)
{
    int i;

    for (i = 0; i < sizeof(sqlstate)/sizeof(sqlstate_t); i++)
        if (strcmp((char*) state, (char*) sqlstate[i].sqlstate) == 0)
            return sqlstate[i].message;

    return (SQLCHAR*) "Unknown error";
}

/*
 * Gets the connection information for the given DSN.
 */
static cinfo_t *
get_conninfo(SQLCHAR *dsn)
{
    cinfo_t *p;

    if (!first)
        return NULL;

    if (!last)
        return first;

    for (p = first; p; p = p->next)
		if (strcmp((char*) p->dsn, (char*) dsn) == 0)
            return p;

    return NULL;
}

/*
 * Connects to the data source name. The user and password are optional.
 */
static PyObject *
_connect(PyObject *pydsn, PyObject *pyuid, PyObject *pypwd, PyObject *pyconn)
{
    SQLCHAR *dsn = NULL;
    SQLCHAR *uid = NULL;
    SQLCHAR *pwd = NULL;
    SQLCHAR *conn = NULL;
    SQLCHAR *state = NULL;
    SQLCHAR *statemsg = NULL;
    cinfo_t *ci;
    SQLRETURN ret;

    dsn = (SQLCHAR *)PyString_AsString(pydsn);

    /* Check if connection to DSN already exists */
    if (get_conninfo(dsn))
    {
        sprintf(msg, "Connection to DSN \'%s\' already exists", dsn);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    /* Allocate new cinfo_t */
    ci = (cinfo_t *)malloc(sizeof(cinfo_t));
    if (ci == NULL)
    {
        sprintf(msg, "Failed to allocate memory for cinfo_t");
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    /* Initialise cinfo_t */
    ci->next = NULL;
    ci->prev = NULL;
    ci->henv = 0;
    ci->hdbc = 0;
    ci->hstmt = 0;
    ci->autocommit = 0;

    if (!first)
        first = ci;
    if (last)
    {
        ci->prev = last;
        ci->prev->next = ci;
    }
    last = ci; 

    ci->dsn = malloc(strlen((char *)dsn) + 1);
	strcpy((char*) ci->dsn, (char *)dsn);
    uid = (SQLCHAR *)PyString_AsString(pyuid);
    pwd = (SQLCHAR *)PyString_AsString(pypwd);
    if (*uid == '\0')
        uid = (SQLCHAR *)NULL;
    if (*pwd == '\0')
        pwd = (SQLCHAR *)NULL;

    ret = SQLAllocHandle(SQL_HANDLE_ENV, SQL_NULL_HANDLE, &ci->henv);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        sprintf(msg, "Failed to allocate an environment handle");
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    ret = SQLSetEnvAttr(ci->henv, SQL_ATTR_ODBC_VERSION, 
        (void *)SQL_OV_ODBC2, 0);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_DBC, ci->henv);
        statemsg = getmsg(state);
        sprintf(msg, "Failed to set the ODBC version " \
            "(%s:%s)", state, statemsg);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    ret = SQLAllocHandle(SQL_HANDLE_DBC, ci->henv, &ci->hdbc);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_DBC, ci->henv);
        statemsg = getmsg(state);
        sprintf(msg, "Failed to allocate a connection handle " \
            "(%s:%s)", state, statemsg);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    conn = (SQLCHAR *)PyString_AsString(pyconn);
	if (*conn == 0)
	{
    	ret = SQLConnect(ci->hdbc, (SQLCHAR*)dsn, SQL_NTS, (SQLCHAR*)uid, SQL_NTS, 
        	(SQLCHAR*)pwd, SQL_NTS);
    	if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    	{
        	state = getstate(SQL_HANDLE_DBC, ci->hdbc);
        	statemsg = getmsg(state);
        	sprintf(msg, "Failed to connect to the data source (%s:%s)", 
            	state, statemsg);
        	PyErr_SetString(PyExc_Exception, msg);
        	return NULL;
    	}
	}
	else
	{
    	ret = SQLDriverConnect(ci->hdbc, (SQLHWND)NULL, (SQLCHAR*)conn, SQL_NTS, NULL, 0, NULL, SQL_DRIVER_NOPROMPT);
    	if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    	{
        	state = getstate(SQL_HANDLE_DBC, ci->hdbc);
        	statemsg = getmsg(state);
        	sprintf(msg, "Failed to connect to the data source (%s:%s)", 
            	state, statemsg);
        	PyErr_SetString(PyExc_Exception, msg);
        	return NULL;
    	}
	}

    ret = SQLAllocHandle(SQL_HANDLE_STMT, ci->hdbc, &ci->hstmt);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_DBC, ci->hdbc);
        statemsg = getmsg(state);
        sprintf(msg, "Failed to allocate a statement handle " \
            "(%s:%s)", state, statemsg);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    Py_INCREF(Py_None);
    return Py_None;
}

/*
 * Disconnects from the data source name
 */
static void
_disconnect(PyObject *pydsn)
{
    cinfo_t *ci;
    SQLCHAR *dsn = NULL;

    dsn = (SQLCHAR *)PyString_AsString(pydsn);

    ci = get_conninfo(dsn);
    if (ci == NULL)
        return;

    if (ci->hstmt)
    {
        SQLCloseCursor(ci->hstmt);
        SQLFreeHandle(SQL_HANDLE_STMT, ci->hstmt);
    }

    SQLDisconnect(ci->dsn);

    if (ci->hdbc)
        SQLFreeConnect (ci->hdbc);

    if (ci->hdbc)
        SQLFreeHandle (SQL_HANDLE_DBC, ci->hdbc);

    if (ci->henv)
        SQLFreeHandle (SQL_HANDLE_ENV, ci->henv);

    /* Fix up linked list */
    if (!ci->prev && !ci->next)
    {
        /* Remove first and last */
        first = last = NULL;
    }
    else if (!ci->prev && ci->next)
    {
        /* Remove first connection */
        first = ci->next;
        ci->next->prev = NULL;
    }
    else if (ci->prev && !ci->next)
    {
        /* Remove last connection */
        last = ci->prev;
        ci->prev->next = NULL;
    }
    else
    {
        /* Remove somewhere in the middle */
        ci->prev->next = ci->next;
        ci->next->prev = ci->prev;
    }

    /* Finally free the connection info structure */
    free(ci);
}

/*
 * Executes the given SQL command
 */
static PyObject *
_execute(PyObject *pydsn, PyObject *pysql)
{
    cinfo_t *ci;
    SQLRETURN ret;
    SQLLEN norows = 0;
    SQLCHAR *state = NULL;
    SQLCHAR *statemsg = NULL;
    SQLCHAR *dsn = NULL;

    dsn = (SQLCHAR *)PyString_AsString(pydsn);

    ci = get_conninfo(dsn);
    if (ci == NULL)
    {
        sprintf(msg, "Failed to get the connection information for \'%s\'",
            (char *)dsn);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    SQLCloseCursor(ci->hstmt);
    ret = SQLExecDirect(ci->hstmt, (UCHAR *)PyString_AsString(pysql), SQL_NTS);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_STMT, ci->hstmt);
        statemsg = getmsg(state);
        sprintf(msg, "Failed to execute the SQL statement " \
            "(%s:%s)", state, statemsg);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    SQLRowCount(ci->hstmt, &norows);

    return PyInt_FromLong(norows);
}

/*
 * Returns the data for the given column number and type
 */
static PyObject *
get_data(SQLHSTMT hstmt, SQLSMALLINT colno, SQLSMALLINT type)
{
	SQLRETURN ret = 0;
	SQLLEN ind;
	size_t size = 0;
    char buf[BUF_SIZE];
    char *data = NULL;
	PyObject *pydata = NULL;
    PyObject *pyval;
	double dval;
	long lval;
	SQL_DATE_STRUCT date;
	SQL_TIME_STRUCT time;
	SQL_TIMESTAMP_STRUCT timestamp;
    int len;
    int buflen;
    int bufsize;

    data = (char *)malloc(BUF_SIZE);
    *data = '\0';
    buflen = 0;
    bufsize = BUF_SIZE;

	while (1)
	{
		ind = 0;
		switch (type)
		{
        /* Python Integer */
		case SQL_C_LONG:
			ret = SQLGetData(hstmt, colno, type, &lval, sizeof(lval), &ind);
            pydata = PyInt_FromLong(lval);
			break;
        /* Python Float */
        case SQL_C_DOUBLE:
			ret = SQLGetData(hstmt, colno, SQL_C_DOUBLE, &dval, sizeof(dval), 
                &ind);
            pydata = PyFloat_FromDouble(dval);
			break;
        /* Python Tuple (year,month,day) */
		case SQL_C_TYPE_DATE:
			ret = SQLGetData(hstmt, colno, type, &date, size, &ind);
            pydata = PyTuple_New(3);
			sprintf(buf, "%4d", date.year);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 0, pyval);
			sprintf(buf, "%02d", date.month);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 1, pyval);
			sprintf(buf, "%02d", date.day);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 2, pyval);
			break;
        /* Python Tuple (hour,minute,second) */
		case SQL_C_TYPE_TIME:
			ret = SQLGetData(hstmt, colno, type, &time, size, &ind);
            pydata = PyTuple_New(3);
			sprintf(buf, "%02d", time.hour);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 0, pyval);
			sprintf(buf, "%02d", time.minute);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 1, pyval);
			sprintf(buf, "%02d", time.second);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 2, pyval);
			break;
        /* Python Tuple (year,month,day,hour,minute,second,millisecond) */
		case SQL_C_TYPE_TIMESTAMP:
			ret = SQLGetData(hstmt, colno, type, &timestamp, size, &ind);
            pydata = PyTuple_New(7);
			sprintf(buf, "%4d", timestamp.year);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 0, pyval);
			sprintf(buf, "%02d", timestamp.month);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 1, pyval);
			sprintf(buf, "%02d", timestamp.day);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 2, pyval);
			sprintf(buf, "%02d", timestamp.hour);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 3, pyval);
			sprintf(buf, "%02d", timestamp.minute);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 4, pyval);
			sprintf(buf, "%02d", timestamp.second);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 5, pyval);
			sprintf(buf, "%06d", (int)timestamp.fraction);
            pyval = PyString_FromString(buf);
            PyTuple_SetItem(pydata, 6, pyval);
			break;
        /* Python String */
		case SQL_C_CHAR:
			ret = SQLGetData(hstmt, colno, type, buf, BUF_SIZE, &ind);
            len = strlen((char *)buf);
            if (buflen + len >= bufsize)
            {
		        data = realloc(data, bufsize += BUF_SIZE);
            }
            memcpy(data + buflen, buf, len);
            buflen += len;
            data[buflen] = '\0';
			break;
		}

		if (ret != SQL_SUCCESS  && ret != SQL_SUCCESS_WITH_INFO)
		{
			pydata = NULL;
			break;
		}
		else if (ind == SQL_NULL_DATA)
			return PyString_FromString((const char *)"");
		else if (ret == SQL_NO_DATA)
			break;
		else if (ret == SQL_SUCCESS_WITH_INFO && ind > 0)
			continue;

        if (type == SQL_C_CHAR)
        {
            pydata = PyString_FromString(data);
        }

		break;
	}

    if (data)
        free(data);

	return pydata;
}

/*
 * Gets the column data for the given column number. 
 * Does the SQL to C datatype mapping and calls get_data().
 */
static PyObject *
get_col_data(SQLHSTMT hstmt, SQLSMALLINT colno)
{
	SQLCHAR colname[MAX_COLUMN_WIDTH];
	SQLSMALLINT coltype;
	SQLULEN colsize;
	SQLSMALLINT colprec;
	SQLSMALLINT colnull;
	SQLRETURN ret;
    PyObject *data;

	ret = SQLDescribeCol (hstmt, colno, colname, MAX_COLUMN_WIDTH, 
		NULL, &coltype, &colsize, &colprec, &colnull);
	if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
	{
		return NULL;
	}

	switch (coltype)
	{
	case SQL_FLOAT:
	case SQL_REAL:
	case SQL_DOUBLE:
		data = get_data(hstmt, colno, SQL_C_DOUBLE);
		break;
	case SQL_BIT:
	case SQL_TINYINT:
	case SQL_SMALLINT:
	case SQL_INTEGER:
		data = get_data(hstmt, colno, SQL_C_LONG);
		break;
	case SQL_DATE:
		data = get_data(hstmt, colno, SQL_C_TYPE_DATE);
		break;
	case SQL_TIME:
		data = get_data(hstmt, colno, SQL_C_TYPE_TIME);
		break;
	case SQL_TIMESTAMP:
		data = get_data(hstmt, colno, SQL_C_TYPE_TIMESTAMP);
		break;
    /* SQL_CHAR, SQL_VARCHAR, SQL_DECIMAL, SQL_NUMERIC, ... etc */
	default:
		data = get_data(hstmt, colno, SQL_C_CHAR);
		break;
	}

	return data;
}

/*
 * Fetches the table rows for the previous select.
 */
static PyObject *
_fetch(PyObject *pydsn, PyObject *pynrows)
{
    cinfo_t *ci;
    SQLSMALLINT nocols = 0;
    SQLRETURN ret;
    SQLCHAR *state = NULL;
    SQLCHAR *dsn = NULL;
    PyObject *rows, *cols, *data;
    int nrows, count;
    int j;

    dsn = (SQLCHAR *)PyString_AsString(pydsn);

    nrows = PyInt_AsLong(pynrows);

    ci = get_conninfo(dsn);
    if (ci == NULL)
    {
        sprintf(msg, "Failed to get the connection information for \'%s\'",
            (char *)dsn);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    ret = SQLNumResultCols(ci->hstmt, &nocols);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_STMT, ci->hstmt);
        sprintf(msg, "Failed to get the result columns " \
            "(SQLSTATE:%s)", state);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    rows = PyList_New(0);

    count = 1;
    while((ret = SQLFetch(ci->hstmt)) == SQL_SUCCESS)
    {
        cols = PyList_New(0);

        for (j = 1; j <= nocols; j++)
        {
            data = get_col_data(ci->hstmt, j);
            if (data == NULL)
            {
                ret = SQL_ERROR;
                break;
            }
            PyList_Append(cols, data);
        }
        PyList_Append(rows, cols);

        if (nrows)
        {
            if (count >= nrows)
                break;
            count++;
        }
    }

    if (ret == SQL_ERROR)
    {
        state = getstate(SQL_HANDLE_STMT, ci->hstmt);
        sprintf(msg, "Failed to fetch data (SQLSTATE:%s)", state);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    return rows;
}

/*
 * Begins a transaction for the given DSN.
 */
static PyObject *
_begin(PyObject *pydsn)
{
    cinfo_t *ci;
    SQLRETURN ret;
    SQLCHAR *state = NULL;
    SQLCHAR *dsn = NULL;

    dsn = (SQLCHAR *)PyString_AsString(pydsn);

    ci = get_conninfo(dsn);
    if (ci == NULL)
    {
        sprintf(msg, "Failed to get the connection information for \'%s\'",
            (char *)dsn);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    if (ci->autocommit == 1)
    {
        sprintf(msg, "A transaction is already in progress on this connection");
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }
 
    ret = SQLSetConnectAttr(ci->hdbc, SQL_ATTR_AUTOCOMMIT, 
        (void *)SQL_AUTOCOMMIT_OFF, SQL_NTS);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_DBC, ci->hdbc);
        sprintf(msg, "Failed to set attribute SQL_AUTOCOMMIT_OFF" \
            "(SQLSTATE:%s)", state);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    ci->autocommit = 1;

    Py_INCREF(Py_None);
    return Py_None;
}

/*
 * Commits the current transaction for the given DSN.
 */
static PyObject *
_commit(PyObject *pydsn)
{
    cinfo_t *ci;
    SQLRETURN ret;
    SQLCHAR *state = NULL;
    SQLCHAR *dsn = NULL;

    dsn = (SQLCHAR *)PyString_AsString(pydsn);

    ci = get_conninfo(dsn);
    if (ci == NULL)
    {
        sprintf(msg, "Failed to get the connection information for \'%s\'",
            (char *)dsn);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    if (ci->autocommit == 0)
    {
        sprintf(msg, "Must begin the transaction before a commit");
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }
 
    ret = SQLEndTran(SQL_HANDLE_DBC, ci->hdbc, SQL_COMMIT);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_DBC, ci->hdbc);
        sprintf(msg, "Failed to commit transactions" \
            "(SQLSTATE:%s)", state);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    ret = SQLSetConnectAttr(ci->hdbc, SQL_ATTR_AUTOCOMMIT, 
        (void *)SQL_AUTOCOMMIT_ON, SQL_NTS);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_DBC, ci->hdbc);
        sprintf(msg, "Failed to set attribute SQL_AUTOCOMMIT_ON" \
            "(SQLSTATE:%s)", state);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    ci->autocommit = 0;

    Py_INCREF(Py_None);
    return Py_None;
}

/*
 * Rolls back the current transaction for the given DSN.
 */
static PyObject *
_rollback(PyObject *pydsn)
{
    cinfo_t *ci;
    SQLRETURN ret;
    SQLCHAR *state = NULL;
    SQLCHAR *dsn = NULL;

    dsn = (SQLCHAR *)PyString_AsString(pydsn);

    ci = get_conninfo(dsn);
    if (ci == NULL)
    {
        sprintf(msg, "Failed to get the connection information for \'%s\'",
            (char *)dsn);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    if (ci->autocommit == 0)
    {
        sprintf(msg, "Must begin the transaction before a rollback");
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    ret = SQLEndTran(SQL_HANDLE_DBC, ci->hdbc, SQL_ROLLBACK);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_DBC, ci->hdbc);
        sprintf(msg, "Failed to rollback transactions (SQLSTATE:%s)", state);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    ret = SQLSetConnectAttr(ci->hdbc, SQL_ATTR_AUTOCOMMIT, 
        (void *)SQL_AUTOCOMMIT_ON, SQL_NTS);
    if (ret != SQL_SUCCESS && ret != SQL_SUCCESS_WITH_INFO)
    {
        state = getstate(SQL_HANDLE_DBC, ci->hdbc);
        sprintf(msg, "Failed to set attribute SQL_AUTOCOMMIT_ON" \
            "(SQLSTATE:%s)", state);
        PyErr_SetString(PyExc_Exception, msg);
        return NULL;
    }

    ci->autocommit = 0;

    return pydsn;
}



